}
//You're given an array of deposit and withdrawal operations on a bank account that starts with
// zero balance. Your task is to detect if at any point the balance of account fallls below zero, and
// at that point function should return true. Otherwise it should return false.
// >>> below_zero([1, 2, 3])
// false
// >>> below_zero([1, 2, -4, 5])
// true
function below_zero(operations){
  let max_balance = operations[0];
  let min_balance = operations[operations.length-1];
  let total_balance = 0;

  for(let i=0; i<operations.length; i++){
    if(operations[i] > max_balance){
      max_balance = operations[i];
      total_balance = operations[i];
    }
  }

  if(min_balance < total_balance){
    return true;
  }
  else{
    return false;
  }
}
//For a given array of input numbers, calculate Mean Absolute Deviation
// around the mean of this dataset.
// Mean Absolute Deviation is the average absolute difference between each
// element and a centerpoint (mean in this case):
// MAD = average | x - x_mean |
// >>> mean_absolute_deviation([1.0, 2.0, 3.0, 4.0])
// 1.0
function mean_absolute_deviation(numbers){
  var mean = numbers[0];
  var mean_absolute_deviation = 0;
  for(var i = 0; i < numbers.length; i++){
    mean += numbers[i];
    mean_absolute_deviation += Math.abs(mean - mean_absolute_deviation);
  }
  return mean_absolute_deviation;
}
//Insert a number 'delimeter' between every two consecutive elements of input array `numbers'
// >>> intersperse([], 4)
// []
// >>> intersperse([1, 2, 3], 4)
// [1, 4, 2, 4, 3]
function intersperse(numbers, delimeter){
  let newNumbers = [];
  for (let i = 0; i < numbers.length; i++) {
    if (numbers[i] === delimeter) {
      newNumbers.push(numbers.splice(i, 1));
    }
  }
  return newNumbers;
}
