// [1, 2, 3, 3, 3, 4, 4]
function rolling_max(numbers){
  let max_value = 0;
  let max_index = 0;
  for(let i = 0; i < numbers.length; i++){
    if(numbers[i] > max_value){
      max_value = numbers[i];
      max_index = i;
    }
  }
  return max_value;
}
