//Given a string, find out how many distinct characters (regardless of case) does it consist of
// >>> count_distinct_characters("xyzXYZ")
// 3
// >>> count_distinct_characters("Jerry")
// 4
function count_distinct_characters(string){
  let count = 0;
  let char_count = 0;
  let char_count_str = string.split("");
  for(let i = 0; i < char_count.length; i++){
    if(char_count[i] === char_count_str[i]){
      char_count_str.splice(i, 1);
    }
    else{
      char_count_str.push(char_count[i]);
      char_count.splice(i, 1);
    }
  }
  return char_count_str.length;
}
