// >>> parse_music("o o| .| o| o| .| .| .| .| o o")
// [4, 2, 1, 2, 2, 1, 1, 1, 1, 4, 4]
function parse_music(music_string){
  let music_array = music_string.split('');
  let beats = 0;
  for(let i = 0; i < music_array.length; i++){
    let current_beat = parseInt(music_array[i]);
    beats += current_beat;
  }
  return beats;
}
