// to the values of the even indicies of l, but sorted.
// >>> sort_even([1, 2, 3])
// [1, 2, 3]
// >>> sort_even([5, 6, 3, 4])
// [3, 6, 5, 4]
function sort_even(l){
  let even_l = l.filter(x => x % 2 === 0);
  let odd_l = l.filter(x => x % 2 !== 0);
  even_l.sort((a, b) => a - b);
  odd_l.sort((a, b) => a - b);
  return even_l.concat(odd_l);
}
