//Return 2^n modulo p (be aware of numerics).
// >>> modp(3, 5)
// 3
// >>> modp(1101, 101)
// 2
// >>> modp(0, 101)
// 1
// >>> modp(3, 11)
// 8
// >>> modp(100, 101)
// 1
function modp(n, p){
  if(n<=0){
    return 0;
  }
  if(p==0){
    return 1;
  }
  if(p==1){
    return n;
  }
  if(p%2==0){
    return (n*modp(n-1, p/2))%p;
  }
  return (n*modp(n-1, p/2))%p;
}
