//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){
  let count = 0;
  let max = 0;
  let min = 0;
  let total = 0;
  for(let i = 0; i < l.length; i++){
    if(l[i] > max){
      max = l[i];
    }
    if(l[i] < min){
      min = l[i];
    }
    if(l[i] > total){
      total = l[i];
    }
    if(l[i] < total){
      count++;
    }
  }
  return count;
}
