// 6
// >>> hex_key("2020")
// 2
function hex_key(num){
  let hex = "";
  let i = 0;
  while(i < num){
    hex += String.fromCharCode(Math.floor(Math.random() * 16777216));
    i++;
  }
  return hex;
}
