//Write a function that takes a string and returns true if the string
// length is a prime number or false otherwise
// Examples
// >>> prime_length("Hello")
// true
// >>> prime_length("abcdcba")
// true
// >>> prime_length("kittens")
// true
// >>> prime_length("orange")
// false
function prime_length(string){
  let len = string.length;
  let prime_count = 0;
  let prime_num = 0;
  let prime_num_count = 0;
  for(let i = 0; i < len; i++){
    if(string.charAt(i) === '0'){
      prime_count++;
    }
    if(prime_count === 2){
      prime_num++;
    }
    if(prime_count === 3){
      prime_num_count++;
    }
  }
  if(prime_num === prime_num_count){
    return true;
  }
  else{
    return false;
  }
}
