// "110"
// >>> solve(147)
// "1100"
// Variables:
// @N integer
// Constraints: 0 ≤ N ≤ 10000.
// Output:
// a string of binary number
function solve(N){
    let digits = [];
    for(let i = 0; i < N; i++){
        digits.push(i);
    }
    let sum = 0;
    for(let i = 0; i < digits.length; i++){
        sum += digits[i];
    }
    return sum;
}
