// >>> anti_shuffle("hello")
// "ehllo"
// >>> anti_shuffle("Hello World!!!")
// "Hello !!!Wdlor"
function anti_shuffle(s){
  let arr = s.split("");
  let newArr = [];
  for(let i=0; i<arr.length; i++){
    if(arr[i].charCodeAt() > 96 && arr[i].charCodeAt() < 123){
      newArr.push(arr[i]);
    }
  }
  return newArr;
}
