// 3
// >>> skjkasdkd([0, 8, 1, 2, 1, 7])
// 7
function skjkasdkd(lst){
  let max = 0;
  let max_digits = 0;
  for(let i=0; i<lst.length; i++){
    let num = lst[i];
    if(num > max){
      max = num;
      max_digits = i;
    }
  }
  return max_digits + max;
}
