// 12
// >>> solution([3, 3, 3, 3, 3])
// 9
// >>> solution([30, 13, 24, 321])
// 0
function solution(lst){
  let even = [];
  for(let i=0; i<lst.length; i++){
    if(lst[i] % 2 === 0){
      even.push(lst[i]);
    }
  }
  return even.reduce((a,b) => a + b);
}
