// "string"
// >>> find_max(["name", "enam", "game"])
// "enam"
// >>> find_max(["aaaaaaa", "bb", "cc"])
// "aaaaaaa"
function find_max(words){
  let max_word = words[0];
  let max_count = 0;
  for(let i=0; i<words.length; i++){
    if(words[i].length > max_word.length){
      max_word = words[i];
      max_count = i;
    }
  }
  return max_word;
}
