// Input to this function is a string represented multiple groups for nested parentheses separated by spaces.
// For each of the group, output the deepest level of nesting of parentheses.
// E.g. (()()) has maximum two levels of nesting while ((())) has three.
// >>> parse_nested_parens(("(()()) ((())) () ((())()())"))
// (std::vector<long>({(long)2, (long)3, (long)1, (long)3}))
std::vector<long> parse_nested_parens(std::string paren_string) {
    std::vector<long> result;
    std::vector<long> stack;
    std::vector<long> level;
    std::vector<long> depth;

    // Traverse the string and build the stack
    for (char c : paren_string) {
        if (c == '(') {
            stack.push_back(c);
        } else if (c == ')') {
            if (stack.back() == '(') {
                stack.pop_back();
            } else {
                result.push_back(stack.back());
                stack.pop_back();
            }
        }
    }

    // Traverse the stack and build the level
    for (auto it = stack.rbegin(); it != stack.rend(); ++it) {
        if (it->first == '(') {
            level.push_back(it->second);
        } else if (it->first == ')') {
            if (level.back() == '(') {
                level.pop_back();
            } else {
                result.push_back(level.back());
                level.pop_back();
            }
        }
    }

    // Traverse the level and build the depth
    for (auto it = level.rbegin(); it != level.rend(); ++it) {
        if (it->first == '(') {
            depth.push_back(it->second);
        } else if (it->first == ')') {
            if (depth.back() == '(') {
                depth.pop_back();
            } else {
                result.push_back(depth.back());
                depth.pop_back();
            }
        }
    }

    return result;

