// Circular shift the digits of the integer x, shift the digits right by shift
// and return the result as a string.
// If shift > number of digits, return digits reversed.
// >>> circular_shift((12), (1))
// ("21")
// >>> circular_shift((12), (2))
// ("12")
std::string circular_shift(long x, long shift) {
    int n = x;
    int m = shift;
    int temp = 0;
    while (m > n) {
        temp = n;
        n = m;
        m = temp % 10;
    }
    std::string result = "";
    while (n > 0) {
        result += n % 10 + '0';
        n /= 10;
    }
    if (m > n) {
        result = "0" + result;
    }
    return result;

