        if (s[i] >= '0' && s[i] <= '9') {
            int digit = s[i] - '0';
            sum += digit;
        } else if (s[i] >= 'a' && s[i] <= 'z') {
            int digit = s[i] - 'a' + 10;
            sum += digit;
        } else if (s[i] >= 'A' && s[i] <= 'Z') {
            int digit = s[i] - 'A' + 26;
            sum += digit;
        } else {
            assert(false);
        }
    }
    return sum;

