// Given a positive integer N, return the total sum of its digits in binary.
// Example
// >>> solve((1000))
// ("1")
// >>> solve((150))
// ("110")
// >>> solve((147))
// ("1100")
// Variables:
// @N integer
// Constraints: 0 ≤ N ≤ 10000.
// Output:
// a string of binary number
std::string solve(long N) {
    std::string result;
    int carry = 0;
    int digit = 0;
    while (N > 0) {
        digit = N % 10;
        result += digit;
        N /= 10;
        carry = 0;
    }
    if (carry > 0) {
        result += carry;
    }
    return result;

