// Given a string 'text', return its md5 hash equivalent string.
// If 'text' is an empty string, return None.
// >>> string_to_md5(("Hello world"))
// "3e25960a79dbc69b674cd4ec67a72c62"
std::optional<std::string> string_to_md5(std::string text) {
    if (text.empty()) {
        return std::nullopt;
    }

    std::string digest = "";
    unsigned char digest_buffer[64];
    unsigned char digest_size = 0;

    // Calculate the MD5 hash of the given text
    for (int i = 0; i < text.length(); i++) {
        unsigned char hash = 0;
        for (int j = 0; j < 16; j++) {
            hash = hash * 37 + text[i] % 256;
        }
        digest_buffer[digest_size++] = hash;
    }

    // Calculate the MD5 hash of the digest buffer
    unsigned char digest_buffer_size = digest_size;
    unsigned char digest_buffer_length = 0;
    for (int i = 0; i < digest_buffer_size; i++) {
        digest_buffer[digest_buffer_length++] = digest_buffer[i];
    }

    // Calculate the MD5 hash of the digest buffer
    unsigned char digest_hash[64];
    memcpy(digest_hash, digest_buffer, digest_buffer_size);

    // Convert the MD5 hash to a string
    std::string digest_string = std::string(digest_hash, digest_hash + digest_buffer_size);

    return digest_string;

