//Check if in given array of numbers, are any two numbers closer to each other than
// given threshold.
// >>> has_close_elements([1.0, 2.0, 3.0], 0.5)
// false
// >>> has_close_elements([1.0, 2.8, 3.0, 4.0, 5.0, 2.0], 0.3)
// true
function has_close_elements(numbers, threshold){
    let count = 0;
    for(let i = 0; i < numbers.length; i++){
        for(let j = i + 1; j < numbers.length; j++){
            if(Math.abs(numbers[i] - numbers[j]) < threshold){
                count++;
            }
        }
    }
    return count > 0;
}
//Input to this function is a string containing multiple groups of nested parentheses. Your goal is to
// separate those group into separate strings and return the array of those.
// Separate groups are balanced (each open brace is properly closed) and not nested within each other
// Ignore any spaces in the input string.
// >>> separate_paren_groups("( ) (( )) (( )( ))")
// ["()", "(())", "(()())"]
function separate_paren_groups(paren_string){
    let result = [];
    let stack = [];
    let current = "";

    for(let i = 0; i < paren_string.length; i++){
        paren_string[i] = paren_string[i].trim();

        if(paren_string[i] === "("){
            stack.push(current);
            current = paren_string[i];
        }else if(paren_string[i] === ")"){
            if(stack.length > 0){
                let last_open = stack.pop();
                if(last_open === "("){
                    current = last_open;
                }else{
                    result.push(current);
                    current = "";
                }
            }else{
                result.push(current);
            }
        }
    }

    if(current !== ""){
        result.push(current);
    }

    return result;
}
//Given a positive floating point number, it can be decomposed into
// and integer part (largest integer smaller than given number) and decimals
// (leftover part always smaller than 1).
// Return the decimal part of the number.
// >>> truncate_number(3.5)
// 0.5
function truncate_number(number){
    let decimal = Math.abs(number);
    let integer = Math.floor(decimal);
    let decimals = Math.abs(decimal - integer);
    let result = integer + decimals;
    return result;
}
//You're given an array of deposit and withdrawal operations on a bank account that starts with
// zero balance. Your task is to detect if at any point the balance of account fallls below zero, and
// at that point function should return true. Otherwise it should return false.
// >>> below_zero([1, 2, 3])
// false
// >>> below_zero([1, 2, -4, 5])
// true
function below_zero(operations){
    let balance = 0;
    for(let i = 0; i < operations.length; i++){
        if(operations[i] > 0){
            balance += operations[i];
        } else if(operations[i] < 0){
            balance -= operations[i];
        }
    }
    return balance < 0;
}
//For a given array of input numbers, calculate Mean Absolute Deviation
// around the mean of this dataset.
// Mean Absolute Deviation is the average absolute difference between each
// element and a centerpoint (mean in this case):
// MAD = average | x - x_mean |
// >>> mean_absolute_deviation([1.0, 2.0, 3.0, 4.0])
// 1.0
function mean_absolute_deviation(numbers){
    let mean = sum(numbers) / numbers.length;
    let sum_deviation = 0;
    for(let i = 0; i < numbers.length; i++){
        sum_deviation += Math.abs(numbers[i] - mean);
    }
    return sum_deviation / numbers.length;
}
//Insert a number 'delimeter' between every two consecutive elements of input array `numbers'
// >>> intersperse([], 4)
// []
// >>> intersperse([1, 2, 3], 4)
// [1, 4, 2, 4, 3]
function intersperse(numbers, delimeter){
    let result = [];
    for(let i = 0; i < numbers.length; i++){
        for(let j = i + 1; j < numbers.length; j++){
            result.push(numbers[i] + numbers[j] + delimeter);
        }
    }
    return result;
}
//Input to this function is a string represented multiple groups for nested parentheses separated by spaces.
// For each of the group, output the deepest level of nesting of parentheses.
// E.g. (()()) has maximum two levels of nesting while ((())) has three.
// >>> parse_nested_parens("(()()) ((())) () ((())()())")
// [2, 3, 1, 3]
function parse_nested_parens(paren_string){
  let result = [];
  let stack = [];
  let current_group = "";
  let current_level = 0;

  for(let i = 0; i < paren_string.length; i++){
    paren_string[i] = paren_string[i].trim();

    if(paren_string[i] == "("){
      current_group += paren_string[i];
      current_level++;
      stack.push(current_group);
    }
    else if(paren_string[i] == ")"){
      let current_group = stack.pop();
      if(current_group == "" && current_level > 0){
        current_level--;
      }
      else if(current_level > 0){
        result.push(current_level);
      }
    }
  }

  return result;
}
//Filter an input array of strings only for ones that contain given substring
// >>> filter_by_substring([], "a")
// []
// >>> filter_by_substring(["abc", "bacd", "cde", "array"], "a")
// ["abc", "bacd", "array"]
function filter_by_substring(strings, substring){
    return strings.filter(function(string){
        return string.includes(substring);
    });
}
//For a given array of integers, return an array consisting of a sum and a product of all the integers in an array.
// Empty sum should be equal to 0 and empty product should be equal to 1.
// >>> sum_product([])
// [0, 1]
// >>> sum_product([1, 2, 3, 4])
// [10, 24]
function sum_product(numbers){
    let sum = 0;
    let product = 1;
    for(let i = 0; i < numbers.length; i++){
        sum += numbers[i];
        product *= numbers[i];
    }
    return [sum, product];
}
//From a given array of integers, generate an array of rolling maximum element found until given moment
// in the sequence.
// >>> rolling_max([1, 2, 3, 2, 3, 4, 2])
// [1, 2, 3, 3, 3, 4, 4]
function rolling_max(numbers){
    let max = numbers[0];
    let max_index = 0;

    for(let i = 1; i < numbers.length; i++){
        if(numbers[i] > max){
            max = numbers[i];
            max_index = i;
        }
    }

    return [max, max_index];
}

