//Find the shortest palindrome that begins with a supplied string.
// Algorithm idea is simple:
// - Find the longest postfix of supplied string that is a palindrome.
// - Append to the end of the string reverse of a string prefix that comes before the palindromic suffix.
// >>> make_palindrome("")
// ""
// >>> make_palindrome("cat")
// "catac"
// >>> make_palindrome("cata")
// "catac"
function make_palindrome(string){
    let longest_palindrome = "";
    let longest_prefix = "";
    let longest_suffix = "";
    let longest_palindrome_length = 0;

    for(let i = 0; i < string.length; i++){
        let char = string[i];
        let palindrome_length = 0;

        for(let j = i; j < string.length; j++){
            let char_next = string[j];
            if(char_next === char){
                palindrome_length++;
            } else {
                if(palindrome_length > longest_palindrome_length){
                    longest_palindrome = char + char_next;
                    longest_palindrome_length = palindrome_length;
                    longest_prefix = char_next;
                    longest_suffix = char;
                }
            }
        }

        if(longest_palindrome_length > longest_palindrome_length){
            longest_palindrome = longest_prefix + char + longest_suffix;
            longest_palindrome_length = palindrome_length;
        }

        if(longest_palindrome_length > longest_suffix.length){
            longest_suffix = char + char_next;
            longest_suffix_length = longest_suffix.length;
        }
    }

    return longest_palindrome;
}
//Input are two strings a and b consisting only of 1s and 0s.
// Perform binary XOR on these inputs and return result also as a string.
// >>> string_xor("010", "110")
// "100"
function string_xor(a, b){
    let result = "";
    for(let i = 0; i < a.length; i++){
        result += a[i] ^ b[i];
    }
    return result;
}
//Out of array of strings, return the longest one. Return the first one in case of multiple
// strings of the same length. Return undefined in case the input array is empty.
// >>> longest([])
// undefined
// >>> longest(["a", "b", "c"])
// "a"
// >>> longest(["a", "bb", "ccc"])
// "ccc"
function longest(strings){
    let longest = strings[0];
    for(let i = 1; i < strings.length; i++){
        if(strings[i].length > longest.length){
            longest = strings[i];
        }
    }
    return longest;
}
//Return a greatest common divisor of two integers a and b
// >>> greatest_common_divisor(3, 5)
// 1
// >>> greatest_common_divisor(25, 15)
// 5
function greatest_common_divisor(a, b){
    while(b > 0){
        let temp = b;
        b = a % b;
        a = temp;
    }
    return a;
}
//Return array of all prefixes from shortest to longest of the input string
// >>> all_prefixes("abc")
// ["a", "ab", "abc"]
function all_prefixes(string){
    let arr = [];
    let longest = string.length;
    let shortest = 0;

    for(let i = 0; i < longest; i++){
        for(let j = 0; j < shortest; j++){
            if(string[i] === string[j]){
                arr.push(string[i]);
                arr.push(string[j]);
                break;
            }
        }
    }

    return arr;
}
//Return a string containing space-delimited numbers starting from 0 upto n inclusive.
// >>> string_sequence(0)
// "0"
// >>> string_sequence(5)
// "0 1 2 3 4 5"
function string_sequence(n){
    let result = '';
    for(let i = 0; i < n; i++){
        result += String(i) + ' ';
    }
    return result.trim();
}
//Given a string, find out how many distinct characters (regardless of case) does it consist of
// >>> count_distinct_characters("xyzXYZ")
// 3
// >>> count_distinct_characters("Jerry")
// 4
function count_distinct_characters(string){
    let count = 0;
    for(let i = 0; i < string.length; i++){
        if(string[i] === string[i].toUpperCase()){
            count++;
        }else{
            count--;
        }
    }
    return count;
}
