// >>> parse_music("o o| .| o| o| .| .| .| .| o o")
// [4, 2, 1, 2, 2, 1, 1, 1, 1, 4, 4]
function parse_music(music_string){
    let beats = [];
    let note_map = {
        'o': 4,
        'o|': 2,
        '.|': 1
    };

    for(let i = 0; i < music_string.length; i++){
        let note = music_string[i];
        let count = 0;
        for(let j = 0; j < note.length; j++){
