// n cars are driving left to right;  simultaneously, a different set of n cars
// are driving right to left.   The two sets of cars start out being very far from
// each other.  All cars move in the same speed.  Two cars are said to collide
// when a car that's moving left to right hits a car that's moving right to left.
// However, the cars are infinitely sturdy and strong; as a result, they continue moving
// in their trajectory as if they did not collide.
// This function outputs the number of such collisions.
function car_race_collision(n){
    let count = 0;
    let cars = [...Array(n).keys()];
    let left_cars = cars.slice(0, n);
    let right_cars = cars.slice(n);
    let left_cars_left = left_cars.map(car => {
        let left_car = car.slice(0, car.length - 1);
        let right_car = car.slice(car.length - 1, car.length);
        return left_car.concat(right_car);
    });
    let right_cars_right = right_cars.map(car => {
        let right_car = car.slice(0, car.length - 1);
        let right_car_left = right_car.slice(0, right_car.length - 1);
        return right_car_left.concat(right_car);
    });

    for(let i = 0; i < left_cars_left.length; i++){
        for(let j = 0; j < right_cars_right.length; j++){
            if(left_cars_left[i] == right_cars_right[j]){
                count++;
            }
        }
    }

    return count;
}
