// fib4(0) -> 0
// fib4(1) -> 0
// fib4(2) -> 2
// fib4(3) -> 0
// fib4(n) -> fib4(n-1) + fib4(n-2) + fib4(n-3) + fib4(n-4).
// Please write a function to efficiently compute the n-th element of the fib4 number sequence.  Do not use recursion.
// >>> fib4(5)
// 4
// >>> fib4(6)
// 8
// >>> fib4(7)
// 14
function fib4(n){
    let fib4 = [];
    for(let i = 0; i < n; i++){
        let fib4_n = 0;
        let fib4_n_prev = 0;
        let fib4_n_curr = 1;
        let fib4_n_next = 1;
        for(let j = 0; j < n - 1; j++){
            fib4_n = fib4_n_prev + fib4_n_curr;
            fib4_n_prev = fib4_n_curr;
            fib4_n_curr = fib4_n;
            fib4_n_next = fib4_n_curr + fib4_n_prev;
        }
        fib4.push(fib4_n);
    }
    return fib4[n];
}
