function median(l){
    let sorted = l.sort((a, b) => a - b);
    let length = sorted.length;
    let mid = length / 2;
    let median = sorted[mid];
    if(length % 2 == 0){
        return (median + median / 2) / 2;
    }
    else{
        return median;
    }
}
