//brackets is a string of "<" and ">".
// return true if every opening bracket has a corresponding closing bracket.
// >>> correct_bracketing("<")
// false
// >>> correct_bracketing("<>")
// true
// >>> correct_bracketing("<<><>>")
// true
// >>> correct_bracketing("><<>")
// false
function correct_bracketing(brackets){
    let stack = [];
    let result = [];

    for(let bracket of brackets){
        if(bracket === "<"){
            stack.push(bracket);
        }
        else if(bracket === ">"){
            if(stack.length > 0){
                let last_bracket = stack.pop();
                result.push(last_bracket);
            }
        }
        else{
            if(stack.length > 0){
                let last_bracket = stack.pop();
                result.push(last_bracket + bracket);
            }
        }
    }

    return result;
}
