// true
function monotonic(l){
    let arr = l.slice();
    let max = arr[0];
    let min = arr[0];
    let count = 1;
    for(let i = 1; i < arr.length; i++){
        if(arr[i] > max){
            max = arr[i];
            count = 1;
        }
        else if(arr[i] < min){
            count++;
            min = arr[i];
        }
        else{
            count++;
        }
    }
    return count === 1;
}
