//Circular shift the digits of the integer x, shift the digits right by shift
// and return the result as a string.
// If shift > number of digits, return digits reversed.
// >>> circular_shift(12, 1)
// "21"
// >>> circular_shift(12, 2)
// "12"
function circular_shift(x, shift){
    let result = '';
    let temp = '';
    let digit = '';
    let digit_count = 0;

    // Convert the integer to string and reverse the string
    for(let i = 0; i < x.toString().length; i++){
        digit = x.toString().charAt(i);
        digit_count++;
        if(digit_count === shift){
            result += digit;
            digit_count = 0;
        }
    }

    // Reverse the string and convert it back to integer
    for(let i = 0; i < result.length; i++){
        temp += result.charAt(result.length - i - 1);
    }

    // Convert the string back to integer
    for(let i = 0; i < temp.length; i++){
        result += parseInt(temp.charAt(i));
    }

    return result;
}
