//You are given a string s.
// Your task is to check if the string is hapjs or not.
// A string is hapjs if its length is at least 3 and every 3 consecutive letters are distinct
// For example:
// >>> is_happy("a")
// false
// >>> is_happy("aa")
// false
// >>> is_happy("abcd")
// true
// >>> is_happy("aabb")
// false
// >>> is_happy("adb")
// true
// >>> is_happy("xyy")
// false
function is_happy(s){
    let count = 0;
    let arr = s.split('');
    let result = [];
    for(let i = 0; i < arr.length; i++){
        if(arr[i] === arr[i+1] && arr[i]!== arr[i+2]){
            count++;
        }
        else if(count > 3){
            result.push(arr[i]);
            count = 1;
        }
    }
    if(count > 3){
        return true;
    }
    else{
        return false;
    }
}
//It is the last week of the semester and the teacher has to give the grades
// to students. The teacher has been making her own algorithm for grading.
// The only problem is, she has lost the code she used for grading.
// She has given you an array of GPAs for some students and you have to write 
// a function that can output an array of letter grades using the following table:
// GPA       |    Letter grade
// 4.0                A+
// > 3.7                A 
// > 3.3                A- 
// > 3.0                B+
// > 2.7                B 
// > 2.3                B-
// > 2.0                C+
// > 1.7                C
// > 1.3                C-
// > 1.0                D+ 
// > 0.7                D 
// > 0.0                D-
// 0.0                E
// Example:
// >>> grade_equation([4.0, 3, 1.7, 2, 3.5])
// ["A+", "B", "C-", "C", "A-"]
function numerical_letter_grade(grades){
    let letter_grades = [];
    for(let i = 0; i < grades.length; i++){
        let grade = grades[i];
        if(grade >= 4.0){
            letter_grades.push("A+");
        } else if(grade >= 3.7){
            letter_grades.push("A");
        } else if(grade >= 3.3){
            letter_grades.push("A-");
        } else if(grade >= 3.0){
            letter_grades.push("B+");
        } else if(grade >= 2.7){
            letter_grades.push("B");
        } else if(grade >= 2.3){
            letter_grades.push("B-");
        } else if(grade >= 2.0){
            letter_grades.push("C+");
        } else if(grade >= 1.7){
            letter_grades.push("C");
        } else if(grade >= 1.3){
            letter_grades.push("C-");
        } else if(grade >= 1.0){
            letter_grades.push("D+");
        } else if(grade >= 0.7){
            letter_grades.push("D");
        } else if(grade >= 0.0){
            letter_grades.push("E");
        } else {
            letter_grades.push("E");
        }
    }
    return letter_grades;
}
