function closest_integer(value){
    let num = parseFloat(value);
    let closest = Math.abs(Math.floor(num));
    let closest_two = Math.abs(Math.floor(num / 2));
    let closest_three = Math.abs(Math.floor(num / 3));

    if(Math.abs(num - closest) < Math.abs(num - closest_two)){
        return closest;
    }else if(Math.abs(num - closest_two) < Math.abs(num - closest_three)){
        return closest_two;
    }else{
        return closest_three;
    }
}
