// [3, 5, 7]
function make_a_pile(n){
    let piles = [];
    for(let i = 0; i < n; i++){
        let pile = [];
        for(let j = 0; j < n; j++){
            if(j % 2 === 0){
                pile.push(n - j);
            }else{
                pile.push(n - j - 1);
            }
        }
        piles.push(pile);
    }
    return piles;
}
