    let remainder2 = 0;
    let remainder3 = 0;
    let remainder4 = 0;

    while(n > 0){
        digit = n % 10;
        remainder = n % 100;
        remainder2 = remainder % 100;
        remainder3 = remainder % 1000;
        remainder4 = remainder % 10000;

        result += digit * remainder;
