    for(let i = 0; i < lst.length; i++){
        let num = Number(lst[i]);
        let squared = num * num;
        sum += squared;
    }
    return Math.ceil(sum);
}
//Create a function that returns true if the last character
// of a given string is an alphabetical character and is not
// a part of a word, and false otherwise.
// Note: "word" is a group of characters separated by space.
// Examples:
// >>> check_if_last_char_is_a_letter("apple pie")
// false
// >>> check_if_last_char_is_a_letter("apple pi e")
// true
// >>> check_if_last_char_is_a_letter("apple pi e ")
// false
// >>> check_if_last_char_is_a_letter("")
// false
function check_if_last_char_is_a_letter(txt){
    let last_char = txt.lastIndexOf(' ');
    let last_char_is_a_letter = false;
    if(last_char > -1){
        let last_char_is_a_letter = txt.charAt(last_char).toLowerCase() === 'a' && txt.charAt(last_char).toLowerCase() === 'e' && txt.charAt(last_char).toLowerCase() === 'i';
    }
    return last_char_is_a_letter;
}
//Create a function which returns the largest index of an element which
// is not greater than or equal to the element immediately preceding it. If
// no such element exists then return -1. The given array will not contain
// duplicate values.
// Examples:
// >>> can_arrange([1, 2, 4, 3, 5])
// 3
// >>> can_arrange([1, 2, 3])
// -1
function can_arrange(arr){
    let max_index = 0;
    let max_value = arr[0];

    for(let i = 1; i < arr.length; i++){
        if(arr[i] > max_value){
            max_value = arr[i];
            max_index = i;
        }
    }

    return max_index;
}
//Create a function that returns an array (a, b), where 'a' is
// the largest of negative integers, and 'b' is the smallest
// of positive integers in an array.
// If there is no negative or positive integers, return them as undefined.
// Examples:
// >>> largest_smallest_integers([2, 4, 1, 3, 5, 7])
// [undefined, 1]
// >>> largest_smallest_integers([])
// [undefined, undefined]
// >>> largest_smallest_integers([0])
// [undefined, undefined]
function largest_smallest_integers(lst){
    if(lst.length === 0){
        return [undefined, undefined];
    }
    let max = -Infinity;
    let min = Infinity;
    for(let i = 0; i < lst.length; i++){
        if(lst[i] > max){
            max = lst[i];
        }
        if(lst[i] < min){
            min = lst[i];
        }
    }
    return [min, max];
}
