// This function will take an array of integers. For all entries in the array, the function shall square the integer entry if its index is a 
// multiple of 3 and will cube the integer entry if its index is a multiple of 4 and not a multiple of 3. The function will not 
// change the entries in the array whose indexes are not a multiple of 3 or 4. The function shall then return the sum of all entries. 
// Examples:
// >>> lst
// [1, 2, 3]
// >>> lst
// []
// >>> lst
// [-1, -5, 2, -1, -5]
function sum_squares(lst){
    let sum = 0;
    for(let i = 0; i < lst.length; i++){
        if(i % 3 === 0){
            sum += (i * i * i);
        }
        if(i % 4 === 0 && i % 3 !== 0){
            sum += (i * i * i * i);
        }
    }
    return sum;
}
//You are given a string representing a sentence,
// the sentence contains some words separated by a space,
// and you have to return a string that contains the words from the original sentence,
// whose lengths are prime numbers,
// the order of the words in the new string should be the same as the original one.
// Example 1:
// >>> words_in_sentence("This is a test")
// "is"
// Example 2:
// >>> words_in_sentence("lets go for swimming")
// "go for"
// Constraints:
// * 1 <= len(sentence) <= 100
// * sentence contains only letters
function words_in_sentence(sentence){
    let words = sentence.split(' ');
    let prime_numbers = [];
    for(let word of words){
        let char_set = new Set();
        for(let char of word){
            char_set.add(char);
        }
        if(char_set.size === 2){
            prime_numbers.push(char_set.size);
        }
    }
    let result = [];
    for(let prime of prime_numbers){
        let word = "";
        for(let i = 0; i < prime; i++){
            word += " ";
        }
        result.push(word);
    }
    return result.join(' ');
}
//Your task is to implement a function that will simplify the expression
// x * n. The function returns true if x * n evaluates to a whole number and false
// otherwise. Both x and n, are string representation of a fraction, and have the following format,
// <numerator>/<denominator> where both numerator and denominator are positive whole numbers.
// You can assume that x, and n are valid fractions, and do not have zero as denominator.
// >>> simplify("1/5", "5/1")
// true
// >>> simplify("1/6", "2/1")
// false
// >>> simplify("7/10", "10/2")
// false
function simplify(x, n){
    let numerator = parseInt(x.split('/')[0]);
    let denominator = parseInt(x.split('/')[1]);
    let result = '';

    if(denominator == 0){
        return false;
    }

    for(let i = 0; i < numerator / denominator; i++){
        result += '1';
    }

    return result.length == 1? true : false;
}
//Write a function which sorts the given array of integers
// in ascending order according to the sum of their digits.
// Note: if there are several items with similar sum of their digits,
// order them based on their index in original array.
// For example:
// >>> order_by_points([1, 11, -1, -11, -12])
// [-1, -11, 1, -12, 11]
// >>> order_by_points([])
// []
function order_by_points(nums){
    let sorted_nums = [];
    let sum = 0;
    let index = 0;

    for(let i = 0; i < nums.length; i++){
        sum += nums[i].toString().split('').reduce((a, b) => a + b.length - 1, 0);
        index++;
    }

    for(let i = 0; i < nums.length; i++){
        if(sum[index] > 0){
            sorted_nums.push(nums[i]);
            sum[index] -= 1;
        }else{
            sorted_nums.push(0);
            index++;
        }
    }

    return sorted_nums;
}
//Write a function that takes an array of numbers as input and returns 
// the number of elements in the array that are greater than 10 and both 
// first and last digits of a number are odd (1, 3, 5, 7, 9).
// For example:
// >>> specialFilter([15, -73, 14, -15])
// 1
// >>> specialFilter([33, -2, -3, 45, 21, 109])
// 2
function specialFilter(nums){
  let oddDigits = [];
  let firstDigits = [];
  let lastDigits = [];

  for(let num of nums){
    if(num % 2 === 0){
      oddDigits.push(num);
    }else{
      firstDigits.push(num);
    }
  }

  for(let num of oddDigits){
    if(num % 2 === 1 && num % 10 % 10 === 1){
      lastDigits.push(num);
    }
  }

  return lastDigits.length + firstDigits.length;
}
//You are given a positive integer n. You have to create an integer array a of length n.
// For each i (1 ≤ i ≤ n), the value of a[i] = i * i - i + 1.
// Return the number of triples (a[i], a[j], a[k]) of a where i < j < k, 
// and a[i] + a[j] + a[k] is a multiple of 3.
// Example :
// >>> get_max_triples(5)
// 1
// Explanation: 
// a = [1, 3, 7, 13, 21]
// The only valid triple is (1, 7, 13).
function get_max_triples(n){
    let a = [1, 3, 7, 13, 21];
    let count = 0;
    for(let i = 1; i < n; i++){
        for(let j = i + 1; j < n; j++){
            for(let k = j + 1; k < n; k++){
                let result = a[i] + a[j] + a[k];
                if(result % 3 == 0){
                    count++;
                }
            }
        }
    }
    return count;
}
