//Given an integer. return an array that has the number of even and odd digits respectively.
// Example:
// >>> even_odd_count(-12)
// [1, 1]
// >>> even_odd_count(123)
// [1, 2]
function even_odd_count(num){
    let count = 0;
    let even = 0;
    let odd = 0;

    for(let i = 0; i < num.toString().length; i++){
        if(num % 10 === 0){
            even++;
            odd = 0;
        }else if(num % 10 % 2 === 0){
            even++;
            odd = 1;
        }else{
            odd++;
        }
    }

    return [even, odd];
}
//Given a positive integer, obtain its roman numeral equivalent as a string,
// and return it in lowercase.
// Restrictions: 1 <= num <= 1000
// Examples:
// >>> int_to_mini_roman(19)
// "xix"
// >>> int_to_mini_roman(152)
// "clii"
// >>> int_to_mini_roman(426)
// "cdxxvi"
function int_to_mini_roman(number){
    let roman_numerals = {
        1: "I",
        4: "IV",
        5: "V",
        9: "IX",
        10: "X",
        40: "XL",
        50: "L",
        90: "XC",
        100: "C",
        400: "CD",
        500: "D",
        900: "CM",
        1000: "M"
    };
    let result = "";
    for(let i = 0; i < number.toString().length; i++){
        let current = number.toString()[i];
        let next = roman_numerals[current];
        if(next){
            result += next;
        }else{
            result += current;
        }
    }
    return result.toLowerCase();
}
