function find_max(words){
    let max_char = 0;
    let max_word = "";
    let max_count = 0;

    words.forEach(word => {
        let char_count = word.length;
        let word_count = word.split("").length - 1;

        if(char_count > max_count){
            max_char = char_count;
            max_word = word;
            max_count = word_count;
        }
    });

    return max_word;
}
