// operator['+', '*', '-']
// array = [2, 3, 4, 5]
// result = 2 + 3 * 4 - 5
// => result = 9
// Note:
// The length of operator array is equal to the length of operand array minus one.
// Operand is an array of of non-negative integers.
// Operator array has at least one operator, and operand array has at least two operands.
function do_algebra(operator, operand){
    let result = 0;
    let operand_length = operand.length;
    let operator_length = operator.length;
    let operand_index = 0;
    let operator_index = 0;
    let result_index = 0;

    while(operand_index < operand_length && operator_index < operator_length){
        let operand_value = operand[operand_index];
        let operator_value = operator[operator_index];

        if(operand_value == 0){
            result = operand_value;
            break;
        }

        if(operand_value % operator_value == 0){
            result = operand_value / operator_value;
            break;
        }

        if(operand_value > operator_value){
            result = operand_value;
            break;
        }

        if(operand_value < operator_value){
            result = operator_value;
            break;
        }

        operand_index++;
        operator_index++;
    }

    if(result == 0){
        result = 0;
    }

    return result;
}

