//You are given a string s.
// if s[i] is a letter, reverse its case from lower to upper or vise versa, 
// otherwise keep it as it is.
// If the string contains no letters, reverse the string.
// The function should return the resulted string.
// Examples
// >>> solve("1234")
// "4321"
// >>> solve("ab")
// "AB"
// >>> solve("#a@C")
// "#A@c"
function solve(s){
    let result = "";
    for(let i = 0; i < s.length; i++){
        if(s[i].match(/[a-z]/)){
            result += s[i].toUpperCase();
        }else{
            result += s[i].toLowerCase();
        }
    }
    return result;
}
//Given a string 'text', return its md5 hash equivalent string.
// If 'text' is an empty string, return undefined.
// >>> string_to_md5("Hello world")
// "3e25960a79dbc69b674cd4ec67a72c62"
function string_to_md5(text){
    let hash = 0;
    for (let i = 0; i < text.length; i++) {
        let char = text[i];
        let ascii_char = String.fromCharCode(char);
        let ascii_digit = parseInt(ascii_char, 16);
        hash += ascii_digit;
    }
    let hash_bytes = Math.abs(hash % 2**32);
    let digest = new Buffer(hash_bytes, 'hex');
    return digest.toString('hex');
}
//Given two positive integers a and b, return the even digits between a
// and b, in ascending order.
// For example:
// >>> generate_integers(2, 8)
// [2, 4, 6, 8]
// >>> generate_integers(8, 2)
// [2, 4, 6, 8]
// >>> generate_integers(10, 14)
// []
function generate_integers(a, b){
    let even_digits = [];
    for(let i = a; i <= b; i++){
        if(i % 2 === 0){
            even_digits.push(i);
        }
    }
    return even_digits;
}
